int main() {
    std::ifstream input("input.txt");
    std::ofstream output("output.txt");
    short size_of_garland, current_number_of_colors = 0;
    garland_colors all_colors[3000];

    std::string current_color;
    input >> size_of_garland;
    bool is_present;
    for (short i = 0; i < size_of_garland; i++) {
        input >> current_color;
        is_present = false;
        for (short k = 0; k < current_number_of_colors; k++) {
            if (all_colors[k].original_name == current_color) {
                is_present = true;
                for (int j = 0; j < current_number_of_colors; j++)
                    if (i - all_colors[j].last_met_coords - 1 < all_colors[j].all_combinations[k]) {
                        all_colors[j].all_combinations[k] = i - all_colors[j].last_met_coords - 1;
                        all_colors[k].all_combinations[j] = i - all_colors[j].last_met_coords - 1;
                    }
                all_colors[k].last_met_coords = i;
                break;
            }
        }
        if (not(is_present)) {
            //добавляем цвет
            all_colors[current_number_of_colors].original_name = current_color;
            all_colors[current_number_of_colors].last_met_coords = i;
            for (short k = 0; k < current_number_of_colors; k++)
                if (i - all_colors[k].last_met_coords - 1 < all_colors[k].all_combinations[current_number_of_colors]) {
                    all_colors[k].all_combinations[current_number_of_colors] = i - all_colors[k].last_met_coords - 1;
                    all_colors[current_number_of_colors].all_combinations[k] = i - all_colors[k].last_met_coords - 1;
                }
            current_number_of_colors++;
        }
    }

    int number_of_pairs, first_index;
    std::string first_color, second_color;
    bool second_is_present;
    input >> number_of_pairs;
    for (int i = 0; i < number_of_pairs; i++) {
        is_present = false;
        second_is_present = false;
        input >> first_color >> second_color;
        for (int k = 0; k < current_number_of_colors; k++)
            if (all_colors[k].original_name == first_color) {
                first_index = k;
                is_present = true;
                break;
            }
        if (is_present)
            for (int k = 0; k < current_number_of_colors; k++)
                if (all_colors[k].original_name == second_color) {
                    second_is_present = true;
                    if (all_colors[k].all_combinations[first_index] == 20000)
                        output << -2 << '\n';
                    else output << all_colors[k].all_combinations[first_index] << '\n';
                    break;
                }
        if (not(second_is_present))
            output << -1 << '\n';
    }

    output.close();
    return 0;
}